import loggingimport warningsfrom time import timefrom forecast_demand_multi_input import forecast_demandfrom multiprocessing_logging import install_mp_handlerwarnings.simplefilter(action='ignore', category=FutureWarning)install_mp_handler()if __name__ == "__main__":    start = time()    logging.basicConfig(level=logging.INFO, filename="output/py_log.log", filemode="a", force=True,                        format="%(asctime)s %(levelname)s %(message)s")    kwargs = {'forecasting_config': 'settings.csv',              'detailed_preferences': 'detailed_preferences.csv',              'forecast_fact_out': 'output//forecast_fact.csv',              'forecast_future_out': 'output//forecast_future.csv',              'meta_parameters_out': 'output//meta_parameters_out.csv'}    forecast_demand('fact.csv', **kwargs)    logging.info(f'Общее время работы python: {time() - start}')    logging.shutdown()